import {React, useEffect, useState, useRef} from 'react'
import { Animator, ScrollContainer, ScrollPage, batch, Fade, FadeIn, FadeOut, Move, MoveIn, MoveOut, Sticky, StickyIn, StickyOut, Zoom, ZoomIn, ZoomOut } from "react-scroll-motion";
import Aos from 'aos'
import "aos/dist/aos.css"
import './About.css'

function About() {

  useEffect(() => {
    Aos.init({duration: 2000});
  }, []);

  const [state, setState] = useState(true);
  const ChangeValueOnScroll = () => {
    const scrollValue = document.documentElement.scrollTop;
    let elementHeight = document.getElementById('About-Container').clientHeight
    if(scrollValue > 0.1 * elementHeight) {
      setState(false);
    }
    else {
      setState(true);
    }
  }
  window.addEventListener('scroll', ChangeValueOnScroll);



  return (
    <ScrollContainer>
        <div id='About-Container' className='About-Container'>
          <div className='About-Holder'>
            <ScrollPage page={0}>
            <Animator animation={batch(Fade(), Move(), Sticky())}>
              <div data-aos="zoom-in-right" className='About-Center-Holder'>
                <div className='About-Text'>
                  <h4>Entrepreneur • Software Engineer • Consultant</h4>
                </div>
                <div className='About-Image'/>
              </div>
                <div data-aos="fade-left" className={ state?'About-Side-Text':'About-Side-Text-Hide'}>
                  <h1 className='About-Title'>Cameron<br/>Saidock</h1>
                  <p className='About-Subtitle'>University of Southern California</p>
                  <div className='Buttons'>
                    <div className='GitHub'>GitHub</div>
                    <div className='LinkedIn'>LinkedIn</div>
                    <div className='Resume'>Resume</div>
                  </div>
                </div>
              </Animator>
            </ScrollPage>
            <ScrollPage page={2}>
              <p data-aos="fade-left" className={state?'About-Info-Text-Hide':'About-Info-Text'}>As a driven and ambitious computer science and business administration student at the University of Southern California, Cameron has a strong background in software engineering and has developed expertise in technologies such as React, Node.js, Python, and C++. In addition to his technical skills, he has gained experience in marketing, strategy consulting, and business development through his involvement with various student organizations and projects. <br/><br/> As the Vice President of Operations & Development for the Residential Housing Association, Cameron demonstrates strong leadership skills and manages a $150,000 annual budget, overseeing professional development initiatives across 14 dormitories and apartment complexes. He is also actively involved in innovative projects, including the development of an attachable safety device for skateboards and an astronomical laser device that can point out constellations from anywhere in the world. <br/><br/> With a passion for entrepreneurship and a desire to make a positive impact on society, Cameron is motivated to use his skills as a software engineer and consultant to create high-functioning startups that will revolutionize the way people view entrepreneurship as a means for implementing change. </p>
            </ScrollPage>
          </div>
        </div>
    </ScrollContainer>
  )
}

export default About
