import {React, useEffect, useState, useRef} from 'react'
import Aos from 'aos'
import "aos/dist/aos.css"
import './About.css'

function About() {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    Aos.init({duration: 2000});
  }, []);

  useEffect(() => {   
    window.addEventListener("scroll", listenToScroll);
    return () => 
       window.removeEventListener("scroll", listenToScroll); 
  }, [])

  const listenToScroll = () => {
    let heightToHideFrom = 200;
    const winScroll = document.body.scrollTop || 
        document.documentElement.scrollTop;

    if (winScroll > heightToHideFrom) { 
       isVisible && setIsVisible(true);
    } else {
         setIsVisible(true);
    }  
  };

  return (
    <div className='About-Holder'>
      <div data-aos="zoom-in-right" className='About-Center-Holder'>
        <div className='About-Text'>
          <h4>Entrepreneur • Software Engineer • Consultant</h4>
        </div>
        <div className='About-Image'/>
      </div>
      {isVisible && <div data-aos="zoom-in-right" className={'About-Side-Text'}>
        <h1 className='About-Title'>Cameron<br/>Saidock</h1>
        <p className='About-Subtitle'>University of Southern California</p>
        <div className='Buttons'>
          <div className='GitHub'>GitHub</div>
          <div className='LinkedIn'>LinkedIn</div>
          <div className='Resume'>Resume</div>
        </div>
      </div>}
      <div className='Bottom'/>
    </div>
  )
}

export default About
