import React, {useRef, useEffect} from 'react'
import './RoleInfo.css'
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

function AIMInfo() {

    const headlineRef = useRef();
    const sectionRef = useRef();
    useEffect(() => {
        gsap.fromTo(
        headlineRef.current,
        {
            autoAlpha: 0,
            x: -40,
        },
        {
            x: 0,
            autoAlpha: 1,
            duration: 1,
            ease: "power3.out",
            scrollTrigger: {
            scroller: ".container",
            trigger: headlineRef.current,
            start: "top 60%",
            end: "bottom 0%",
            toggleActions: "play none restart reverse",
            },
        }
        );
        return () => {};
    }, []);

  return (
    <div className='Role-Container'>
        <div className='Role-Overview'>
            <div id='AIMLogo' className='Role-Image'/>
            <div className='Role-Resume'>
                <div className='Role-Grid'>
                    <div className='Role-Title' id='AIMTitle'>
                        <h1>Assoc. of Innovative Marketing</h1>
                        <h4>Business Marketing Consultant</h4>
                        <h4>Feb 2022 - Present</h4>
                    </div>
                    <div className='Role-Description' id='AIMDescription'>
                        <div className='Role-Subimages'>
                            <h3>Doordash</h3>
                        </div>
                        <div className='Role-Bulletpoints'>
                            <ul>
                                <li>Developed go-to-market strategies focused on optimization of social channels, brand identity, and influencer partnerships for client, Doordash.</li>
                                <li>Conducted primary market research and competitor analysis to sculpt Doordash’s social media personality for Generation-Z, improving engagement and perception.</li>
                            </ul>
                        </div>
                        <div className='Role-Subimages'>
                            <h3>Octi / Ultraviolet</h3>
                        </div>
                        <div className='Role-Bulletpoints'>
                            <ul>
                                <li>Collaborated with project’s electrical team to create software for an attachable safety device on skateboards, providing headlights and blind spot detection using an Arduino.</li>
                                <li>Applied in-depth understanding of C++ to calculate distance between objects and the skateboard to best determine when to turn on blind spot detection.</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
  )
}

export default AIMInfo
