import {React, useEffect, useState, useRef} from 'react'
import Aos from 'aos'
import './Experience.css'
import './flip-Transition.css'
import "aos/dist/aos.css"
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

function Experience() {
    useEffect(() => {
        Aos.init({duration: 2000});
    }, []);

    /* const [state, setState] = useState(true);
    const handleClick = () => {
        let elementHeight = document.getElementByClass('Card')
        if(elementHeight) {
            setState(false);
        }
        else {
            setState(true);
        }
    } */

    const headlineRef = useRef();
    const sectionRef = useRef();
    useEffect(() => {
        gsap.fromTo(
        headlineRef.current,
        {
            autoAlpha: 0,
            x: -40,
        },
        {
            x: 0,
            autoAlpha: 1,
            duration: 1,
            ease: "power3.out",
            scrollTrigger: {
            scroller: ".container",
            trigger: headlineRef.current,
            start: "top 60%",
            end: "bottom 0%",
            toggleActions: "play none restart reverse",
            },
        }
        );
        return () => {};
    }, []);

    const [showFront, setShowFront] = useState(true);

  return (
    <div className='Experience-Container' ref={sectionRef}>
        <div className='Experience-Cards' ref={headlineRef}>
            <div className='Card' id='TroyLabs' onClick={onClick}>
                <div className='Card-Transition-Front'>
                    <div className='Card-Top'/>
                    <div className='Card-Center'/>
                    <div className='Card-Bottom'/>
                    <div className='Card-Category'>
                        <h4>TroyLabs</h4>
                    </div>
                </div>
                <div className='Card-Transition-Back'>

                </div>
            </div>
            <div className='Card' id='Makers'>
                <div className='Card-Transition-Front'>
                    <div className='Card-Top'/>
                    <div className='Card-Center'/>
                    <div className='Card-Bottom'/>
                    <div className='Card-Category'>
                        <h4>USC Makers</h4>
                    </div>
                </div>
                <div className='Card-Transition-Back'>
                    <div className='Card-Back'>
                        <div className='Card-Back-Top'>
                            <h2>Associate Director of Technical Resources</h2>
                        </div>
                        <div className='Card-Back-Description'>
                            <ul>
                                <li>Design software for an astronomical device used to direct lasers to point out constellations and stars relative to user.</li>
                                <li>Build a front-end GUI using React.js which controls all operations of device and saves a list of known constellations and stars based on current location.</li>
                                <li>Calculate location of stars and constellations relative to location of device using data from SIMBAD astronomical database and Pythonâ€™s Astropy package.</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
            <div className='Card' id='AIM'>
                <div className='Card-Transition-Front'>
                    <div className='Card-Top'/>
                    <div className='Card-Center'/>
                    <div className='Card-Bottom'/>
                    <div className='Card-Category'>
                        <h4>AIM</h4>
                    </div>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Experience
