import {React, useLayoutEffect, useState, useRef} from 'react'
import './About.css'

function About() {

  const [show, doShow] = useState({
    AboutCenterHolder: true,
    SideText: false
  });

  const Container = useRef(null)

  useLayoutEffect(() => {
    const topPos = element => element.getBoundingClientRect().top;
    const div1Pos = topPos(Container.current)

    const onScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight;
      if (div1Pos > scrollPos) {
        doShow(state => ({ ...state, SideText: false }));
      }
    };

    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className='About-Holder' ref={Container}>
      <div className='About-Center-Holder' animate={show.AboutCenterHolder}>
        <div className='About-Text'>
          <h4>Entrepreneur • Software Engineer • Consultant</h4>
        </div>
        <div className='About-Image'/>
      </div>
      <div className='About-Side-Text' animate={show.SideText}>
        <h1 className='About-Title'>Cameron <br/> Saidock</h1>
        <p className='About-Subtitle'>University of Southern California</p>
        <div className='Buttons'>
          <div className='GitHub'>GitHub</div>
          <div className='LinkedIn'>LinkedIn</div>
          <div className='Resume'>Resume</div>
        </div>
      </div>
    </div>
  )
}

export default About
