import {React, useEffect, useState, useRef} from 'react'
import { Animator, ScrollContainer, ScrollPage, Sticky } from 'react-scroll-motion'
import Aos from 'aos'
import { gsap } from 'gsap'
import { ScrollTrigger } from 'gsap/dist/ScrollTrigger'
import "aos/dist/aos.css"
import './About.css'

gsap.registerPlugin(ScrollTrigger)

function About() {

  useEffect(() => {
    Aos.init({duration: 2000});
  }, []);

  const headlineRef = useRef();
  const [state, setState] = useState(true);
  const ChangeValueOnScroll = () => {
    const scrollValue = document.documentElement.scrollTop;
    let elementHeight = document.getElementByClass('About-Container').clientHeight;
    let aboutPhoto = document.getElementByClass('About-Image');
    if(scrollValue > 80) {
      setState(false);
    }
    else {
      setState(false);
    }
  }
  window.addEventListener('scroll', ChangeValueOnScroll);
  useEffect(() => {
    let bottom = document.getElementByClass('Bottom');
    gsap.fromTo(
      bottom.cr,
      {
        autoAlpha: 1,
        y: 0
      },
      {
        autoAlpha: 0.25,
        y: -40,
        duration: 1,

        scrollTrigger: {
          scroller: ".container",
          trigger: headlineRef.current,
          start: "top 60%",
          end: "bottom 0%",
          toggleActions: "play none restart reverse"
        },
      }
    )
  }, [])

  //data-aos="fade-left" 
//data-aos="zoom-in-right" 
  return (
    <div className='About-Container'>
      <div className='About-Holder'>
        <div className='About-Center-Holder' ref={headlineRef}>
          <div className='About-Text'>
            <h4>Entrepreneur • Software Engineer • Consultant</h4>
          </div>
          <div className='About-Image'/>
        </div>
        <div className={ state?'About-Side-Text':'About-Side-Text-Hide'}>
          <h1 className='About-Title'>Cameron<br/>Saidock</h1>
          <p className='About-Subtitle'>University of Southern California</p>
          <div className='Buttons'>
            <div className='GitHub'>GitHub</div>
            <div className='LinkedIn'>LinkedIn</div>
            <div className='Resume'>Resume</div>
          </div>
        </div>
          <p className={state?'About-Info-Text-Hide':'About-Info-Text'}>As a driven and ambitious computer science and business administration student at the University of Southern California, Cameron has a strong background in software engineering and has developed expertise in technologies such as React, Node.js, Python, and C++. In addition to his technical skills, he has gained experience in marketing, strategy consulting, and business development through his involvement with various student organizations and projects. <br/><br/> As the Vice President of Operations & Development for the Residential Housing Association, Cameron demonstrates strong leadership skills and manages a $150,000 annual budget, overseeing professional development initiatives across 14 dormitories and apartment complexes. He is also actively involved in innovative projects, including the development of an attachable safety device for skateboards and an astronomical laser device that can point out constellations from anywhere in the world. <br/><br/> With a passion for entrepreneurship and a desire to make a positive impact on society, Cameron is motivated to use his skills as a software engineer and consultant to create high-functioning startups that will revolutionize the way people view entrepreneurship as a means for implementing change. </p>
      </div>
      <div className='Bottom'/>
    </div>
  )
}

export default About
